import { makeStyles } from "@material-ui/styles";
import React, { useState } from "react";
import {
  Grid,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stepper,
  Step,
  StepLabel,
  Button,
} from "@material-ui/core";
import _ from "lodash";
import { KeyboardDatePicker } from "@material-ui/pickers";

const useStyles = makeStyles((theme) => ({
  icon: {
    height: 15,
    width: 15,
  },
  mainHeading: {
    font: "normal normal 500 25px/30px Poppins",
    letterSpacing: "0.9px",
    color: "#62657B",
  },
  subHeading: {
    font: "normal normal 500 22px/25px Poppins",
    letterSpacing: "0.72px",
    color: "#707070",
  },
  answer: {
    font: "normal normal normal 17px/22px Poppins",
    letterSpacing: "0.66px",
    color: "#707070",
  },
  grid: {
    borderBottom: "1px solid rgb(0,0,0,.12)",
    paddingBottom: 20,
  },
}));

const form = [
  {
    first_name: {
      type: "string",
      required: true,
      read_only: false,
      label: "First name",
      max_length: 30,
    },
    last_name: {
      type: "string",
      required: true,
      read_only: false,
      label: "Last name",
      max_length: 150,
    },
    email: {
      type: "email",
      required: true,
      read_only: false,
      label: "Email",
    },
    address: {
      type: "string",
      required: true,
      read_only: false,
      label: "Address",
    },
    city: {
      type: "string",
      required: true,
      read_only: false,
      label: "City",
    },
    state: {
      type: "string",
      required: true,
      read_only: false,
      label: "State",
    },
    zip_code: {
      type: "string",
      required: true,
      read_only: false,
      label: "Zip code",
    },
    country: {
      type: "string",
      required: true,
      read_only: false,
      label: "Country",
    },
  },
  {
    phone_number: {
      type: "string",
      required: true,
      read_only: false,
      label: "Phone number",
      max_length: 128,
    },
    type: {
      type: "choice",
      required: true,
      read_only: false,
      label: "Account Type",
      choices: [
        {
          value: "Current",
          display_name: "Current",
        },
        {
          value: "Savings",
          display_name: "Savings",
        },
      ],
    },
    pan: {
      type: "string",
      required: true,
      read_only: false,
      label: "Pan number",
    },
    aadhar: {
      type: "string",
      required: true,
      read_only: false,
      label: "Aadhar number",
    },
    company: {
      type: "string",
      required: true,
      read_only: false,
      label: "Current Company",
    },
    dob: {
      type: "date",
      required: true,
      read_only: false,
      label: "Date of Birth",
    },
  },
];
const isMultiline = new Set(["address"]);

const QuestionAnswer = ({ question, answer }) => {
  const classes = useStyles();
  return (
    <div>
      <div className={classes.subHeading}>{question}</div>
      <br />
      <div className={classes.answer}>{answer}</div>
    </div>
  );
};
export default (props) => {
  const classes = useStyles();
  const [data, setData] = useState({});
  const [step, setStep] = useState(0);

  const getFormComponent = (key, field) => {
    switch (field.type) {
      case "email":
      case "string":
        return (
          <Grid item xs={12} key={key}>
            <TextField
              type={field.type}
              value={data[key] || ""}
              onChange={(e) => setData({ ...data, [key]: e.target.value })}
              label={_.startCase(field.label || key)}
              variant="outlined"
              required={field.required}
              fullWidth
              multiline={isMultiline.has(key) || false}
              rows={isMultiline.has(key) ? 2 : 1}
              id={key}
              name={key}
              autoComplete={key}
            />
          </Grid>
        );
      case "date":
        return (
          <Grid item xs={12} key={key}>
            <KeyboardDatePicker
              disableToolbar
              fullWidth
              value={data[key]}
              onChange={(e) => setData({ ...data, [key]: e.target.value })}
              label={_.startCase(field.label || key)}
              inputVariant="outlined"
              format="yyyy-MM-dd"
              margin="normal"
              id={key}
              KeyboardButtonProps={{
                "aria-label": "change date",
              }}
            />
          </Grid>
        );

      case "choice":
        return (
          <Grid item xs={12} key={key}>
            <FormControl variant="outlined" required={field.required} fullWidth>
              <InputLabel>{_.startCase(field.label || key)}</InputLabel>
              <Select
                label={_.startCase(field.label || key)}
                value={data[key] || ""}
                onChange={(e) => setData({ ...data, [key]: e.target.value })}
              >
                {field.choices.map((c) => (
                  <MenuItem value={c.value} key={c.value}>
                    {c.display_name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        );
    }
  };

  return (
    <>
      <Stepper activeStep={step}>
        <Step>
          <StepLabel>Personal details</StepLabel>
        </Step>
        <Step>
          <StepLabel>Account details</StepLabel>
        </Step>
        <Step>
          <StepLabel>Summary</StepLabel>
        </Step>
      </Stepper>
      <>
        {step < 2 && (
          <div style={{ padding: 20 }}>
            <Grid container direction="column" spacing={2}>
              {Object.keys(form[step]).map((key) =>
                getFormComponent(key, form[step][key])
              )}
            </Grid>
            <br />
            <br />
            <div style={{ textAlign: "center" }}>
              <Button
                variant="contained"
                color="primary"
                onClick={() => setStep(step + 1)}
              >
                Next
              </Button>
            </div>
          </div>
        )}
        {step === 2 && (
          <>
            <Grid container direction="column">
              <Grid item>
                <div style={{ padding: 20 }}>
                  <div style={{ float: "left" }}>
                    <h3>Personal Details</h3>
                  </div>
                  <div style={{ float: "right" }}>
                    <h3>Personal Details</h3>
                  </div>
                </div>
                <Grid container justify="space-evenly">
                  {Object.keys(form[0]).map((key) => (
                    <Grid item xs={5} key={key} className={classes.grid}>
                      <QuestionAnswer
                        question={form[0][key].label}
                        answer={data[key]}
                      />
                    </Grid>
                  ))}
                </Grid>
              </Grid>
            </Grid>
          </>
        )}
      </>
    </>
  );
};
